const fs = require('fs');
const path = 'logs/secret_rotation.json';
if (fs.existsSync(path)) {
  const logs = JSON.parse(fs.readFileSync(path, 'utf8'));
  const last = logs[logs.length-1];
  if (!last || last.status !== 'SUCCESS' || (Date.now() - new Date(last.timestamp).getTime()) > 25*3600*1000) {
    return [{ alert: true, last_status: last ? last.status : 'none' }];
  }
}
return [{ alert: false }];
